// src/components/Planilha.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Planilha = () => {
  const [driver, setDriver] = useState('');
  const [phone, setPhone] = useState('');
  const [owner, setOwner] = useState('');
  const [drivers, setDrivers] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/db.json')
      .then(response => response.json())
      .then(data => setDrivers(data.drivers));
  }, []);

  const handleAddOrEditDriver = () => {
    if (driver && phone && owner) {
      if (editIndex !== null) {
        const updatedDrivers = [...drivers];
        updatedDrivers[editIndex] = { driver, phone, owner };
        setDrivers(updatedDrivers);
        setEditIndex(null);
      } else {
        setDrivers([...drivers, { driver, phone, owner }]);
      }
      setDriver('');
      setPhone('');
      setOwner('');
    } else {
      alert('Por favor, preencha todos os campos.');
    }
  };

  const handleEditDriver = (index) => {
    setDriver(drivers[index].driver);
    setPhone(drivers[index].phone);
    setOwner(drivers[index].owner);
    setEditIndex(index);
  };

  const handleDeleteDriver = (index) => {
    const updatedDrivers = drivers.filter((_, i) => i !== index);
    setDrivers(updatedDrivers);
  };

  const handlePhoneInput = (e) => {
    const x = e.target.value.replace(/\D/g, '').match(/(\d{0,2})(\d{0,5})(\d{0,4})/);
    e.target.value = !x[2] ? x[1] : `(${x[1]}) ${x[2]}${x[3] ? '-' + x[3] : ''}`;
    setPhone(e.target.value);
  };

  return (
    <div>
      <h1>Cadastro de Motoristas</h1>
      <input
        type="text"
        placeholder="Nome do Motorista"
        value={driver}
        onChange={(e) => setDriver(e.target.value)}
      />
      <input
        type="text"
        placeholder="Telefone"
        value={phone}
        onChange={handlePhoneInput}
      />
      <input
        type="text"
        placeholder="Frotista (ProprietÃ¡rio)"
        value={owner}
        onChange={(e) => setOwner(e.target.value)}
      />
      <button onClick={handleAddOrEditDriver}>
        {editIndex !== null ? 'Editar' : 'Adicionar'}
      </button>
      <button onClick={() => navigate('/motoristas')}>Ver Lista</button>
    </div>
  );
};

export default Planilha;
